sidebarCSS:
#root.root {--rvn: #E40A62}
#root.root {--rmarg: 30px}
#root.root {--rct: 85%}

.TabsPanel:has(div[data-color="toolbar"]) {
  background-color: color-mix(in srgb, transparent var(--rct), var(--toolbar-fg));
}

.TabsPanel:has(div[data-color="blue"]) {
  background-color: color-mix(in srgb, transparent var(--rct), #37adff);
}

.TabsPanel:has(div[data-color="turquoise"]) {
  background-color: color-mix(in srgb, transparent var(--rct),  #00c79a);
}

.TabsPanel:has(div[data-color="green"]) {
  background-color: color-mix(in srgb, transparent var(--rct), #51cd00);
}

.TabsPanel:has(div[data-color="yellow"]) {
  background-color: color-mix(in srgb, transparent var(--rct), #ffcb00);
}

.TabsPanel:has(div[data-color="orange"]) {
  background-color: color-mix(in srgb, transparent var(--rct), #ff9f00);
}

.TabsPanel:has(div[data-color="red"]) {
  background-color: color-mix(in srgb, transparent var(--rct), #ff613d);
}

.TabsPanel:has(div[data-color="pink"]) {
  background-color: color-mix(in srgb, transparent var(--rct), #ff4bda);
}

.TabsPanel:has(div[data-color="purple"]) {
  background-color: color-mix(in srgb, transparent var(--rct), #c071fc);
}




