div.bottom-space {
 display: none;
}

div.TabsPanel > div:nth-child(4){
 background-color: transparent;
}

div.AnimatedTabList div.new-tab-btns {
background-color: rgba(100,100,100,0.0);


}


.Tab .ctx { background-color: transparent;
}

#root.root {--tabs-color-layer-opacity: 0.25;}
#root.root {--tabs-lvl-opacity: 0.3;}

#root.root {--tabs-activated-color-layer-opacity: 0.25;}#root.root {--frame-el-overlay-selected-border: var(--rvn);}
#root.root {--toolbar-el-overlay-selected-border: var(--rvn);}
#root.root {--accent: var(--rvn);}
#root.root {--tabs-height: var(--rmarg);}
#root.root {--nav-btn-height: var(--rmarg);}
#root.root {--nav-btn-width: var(--rmarg);}
#root.root {--ntb-btn-height: var(--rmarg);}
#root.root {--bookmarks-bookmark-height: var(--rmarg);}
#root.root {--bookmarks-folder-height: var(--rmarg);}
