#root.root {--tabs-normal-fg: rgb(190, 190, 190);}
#root.root {--tabs-activated-bg: rgb(32, 32, 32);}
#root.root {--tabs-progress-bg: rgba(255,255,255,0.086);}
#root.root {--toolbar-bg: rgba(0, 0, 0, 0);}
#root.root {--tabs-font: 0.9rem "ibm plex sans condensed";}
#root.root {--tabs-count-font: 0.5rem "ibm plex mono";}
