#root.root {--tabs-activated-fg: rgb(255, 255, 255);}




#root.root {--ntb-border-radius: 0px;}
#root.root {--slt-border-radius: 0px;}

#root.root {--tooolbar-border-radius: 0px;}
#root.root {--bottom-bar-height: var(--rmarg);}



#root.root {--tabs-normal-bg: #2b2b2b5D;}











#root.root {--tabs-indent: 12px;}
#root.root {--tabs-lvl-opacity: 0.3;}



#root.root {--status-active: #ff0080ff;}

