#root.root {--btn-border-radius: 0px;}
#root.root {--notification-border-radius: 0px;}
#root.root {--search-height: 30px;}

#root.root {--ctx-menu-border-radius: 0px;}
#root.root {--tabs-margin: 2px;}


#root.root {--tabs-pinned-height: var(--rmarg);}



#root.root {--frame-el-overlay-hover-bg: rgba(255,255,255,0.086);}


















#root.root {--d-slow: 0.005s;}













#root.root {--general-border-radius: 0px;}









#root.root {--nav-btn-border-radius: 0px;}

#root.root {--ctx-menu-bg: #000000ff;}
#root.root {--ctx-menu-opt-border-radius: 0px;}

#root.root {--bookmarks-node-activated-fg: #00ffffff;}

@media (max-width: 80px) {
	.TabsPanel {
		--tabs-indent: 0px; 
	}
}

.TabsPanel .new-tab-btns {
	padding-right: 0 var(--tabs-margin) !important;
}

#root .Tab {
	margin-left: 1.5px !important;
	margin-right: 1.5px !important;
}

#new-tab-btn {
	width: 100% !important;
}

.NavigationBar {
	border-radius: 6px !important;
	margin: 0.3px !important

}

